import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./UserPage.css";
import Cookies from 'js-cookie';
import { decodeToken } from "react-jwt";

const UserPage = () => {
  const [useremail, setUseremail] = useState('');
  const [username, setUsername] = useState('');
  const [order, setOrder] = useState({}); // State is now a single object
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const token = Cookies.get('authToken');
    if (token) {
      try {
        const decoded = decodeToken(token);
        setUseremail(decoded.email);
        setUsername(decoded.name);
        fetchOrders(decoded.email);
      } catch (error) {
        console.error("Token validation failed", error);
      }
    } else {
      console.log("No user details found");
    }
  }, []);

  const fetchOrders = async (email) => {
    try {
      const response = await axios.get(`http://localhost:3020/fetchorders/${email}`);
      console.log(response.data)
      if (response.data || response.data.productDetails) {
        setOrder(response.data[0]); 
        console.log(order)// Set the single order object
      } else {
        setOrder({}); // If no order found, set to an empty object
        setErrorMessage("No product details found.");
      }
    } catch (error) {
      console.error("Error fetching orders:", error);
      setErrorMessage("Failed to fetch orders.");
    }
  };

  const handleLogout = async () => {
    Cookies.remove('authToken');
    navigate('/');
  };

  return (
    <div className="user-page-container">
      <h2 className="h2">User Dashboard</h2>
      {errorMessage && <div className="error-message">{errorMessage}</div>}

      {username || useremail ? (
        <div className="user-details">
          <h3>My Details</h3>
          <p><strong>Name:</strong> {username}</p>
          <p><strong>Email:</strong> {useremail}</p>
        </div>
      ) : (
        <p>Loading user details...</p>
      )}

      <div className="user-orders">
        <h3>My Orders:</h3>
        {Object.keys(order).length > 0 ? (
          <div>
            <strong>Product ID:</strong> {order.ProductId} <br />
            <strong>Quantity:</strong> {order.qty} <br />
            {/* You can add more product details here */}
            {/* Example: */}
            {/* <strong>Product Name:</strong> {order.productName} <br /> */}
          </div>
        ) : (
          <p>You have no orders yet.</p>
        )}
      </div>

      <button onClick={handleLogout} className="logout-button">Logout</button>
    </div>
  );
};

export default UserPage;
