import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./UserPage.css";

const UserPage = () => {
  const [userDetails, setUserDetails] = useState(null);
  const [orders, setOrders] = useState([]);
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const response = await axios.get("http://localhost:3020/user", {
          withCredentials: true,
        });
        setUserDetails(response.data.user);
        setOrders(response.data.orders);
      } catch (error) {
        setErrorMessage(
          error.response?.data?.message || "Failed to fetch user details."
        );
      }
    };

    fetchUserDetails();
  }, []);

  const handleLogout = async () => {
    try {
      await axios.post("http://localhost:3020/logout", {}, { withCredentials: true });
      navigate("/login");
    } catch (error) {
      setErrorMessage("Failed to logout. Please try again.");
    }
  };

  return (
    <div className="user-page-container">
      < h2 className="h2"> User Dashboard</h2>
      {errorMessage && <div className="error-message">{errorMessage}</div>}

      {userDetails ? (
        <div className="user-details">
          <h3>My Details</h3>
          <p><strong>Name:</strong> {userDetails.firstName} {userDetails.lastName}</p>
          <p><strong>Email:</strong> {userDetails.email}</p>
        </div>
      ) : (
        <p>Loading user details...</p>
      )}

      <div className="user-orders">
        <h3>My Orders</h3>
        {orders.length > 0 ? (
          <ul>
            {orders.map((order, index) => (
              <li key={index}>
                <strong>Order ID:</strong> {order.id} <br />
                <strong>Date:</strong> {new Date(order.date).toLocaleString()} <br />
                <strong>Status:</strong> {order.status}
              </li>
            ))}
          </ul>
        ) : (
          <p>You have no orders yet.</p>
        )}
      </div>

      <button onClick={handleLogout} className="logout-button">Logout</button>
    </div>
  );
};

export default UserPage;
