import React, { useState, useEffect } from "react";
import "./Home.css";

const Home = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [images,setimages] = useState(['https://www.google.com/imgres?q=clothing%20images&imgurl=https%3A%2F%2Fwww.cabionline.com%2Fwp-content%2Fuploads%2F2024%2F07%2Fhomepage.jpg&imgrefurl=https%3A%2F%2Fwww.cabionline.com%2F&docid=QFGErqoQvJbTeM&tbnid=JOy7B4Mzb-RyPM&vet=12ahUKEwirs5vSu7aKAxUMTWcHHTu1Lt4QM3oECHMQAA..i&w=1200&h=630&hcb=2&ved=2ahUKEwirs5vSu7aKAxUMTWcHHTu1Lt4QM3oECHMQAA']);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) =>
        prevIndex === images.length - 1 ? 0 : prevIndex + 1
      );
    }, 5000); // Change image every 5 seconds
    return () => clearInterval(interval); // Cleanup on component unmount
  }, [images.length]);

  return (
    <div className="slideshow-container">
      {images.map((image, index) => (
        <div
          key={index}
          className={`slide ${index === currentIndex ? "active" : ""}`}
          style={{
            backgroundImage: `url(${image})`,
          }}
        ></div>
      ))}
    </div>
  );
};

export default Home;
