import { useState, useEffect } from 'react';
import axios from 'axios';
import './Saree.css';

const Bestseller = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:3020/fetchproductsbest');
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  const handleAddToCart = (productId) => {
    console.log(`Add to cart clicked for product: ${productId}`);
    // Implement the add-to-cart functionality here
  };

  const handleAddToWishlist = (productId) => {
    console.log(`Add to wishlist clicked for product: ${productId}`);
    // Implement the wishlist functionality here
  };

  return (
    <div className="buyer-saree-product-grid-container">
      <h1>Product List</h1>
      <div className="buyer-saree-product-grid">
        {products.map((product) => (
          <div key={product._id} className="buyer-saree-product-item">
            <img
              src={product.image}
              alt={product.title}
              className="buyer-saree-product-image"
            />
            <h3>{product.title}</h3>
            <p>Price: ${product.price}</p>
            <p>Category: {product.category}</p>
            <div className="buyer-saree-product-actions">
              <button
                onClick={() => handleAddToCart(product._id)}
                className="buyer-saree-product-button"
              >
                Add to Cart
              </button>
              <button
                onClick={() => handleAddToWishlist(product._id)}
                className="buyer-saree-product-button"
              >
                Wishlist
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Bestseller;
