import {useState,useEffect} from 'react';
import './Products.css'
import axios from 'axios'
import Cookies from 'js-cookie';
import {decodeToken} from 'react-jwt'
import {useNavigate} from 'react-router-dom';
import './SellerProductsPage.css';


const SellerProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [productname, setproductname] = useState('');
  const [productprice , setproductprice] = useState('');
  const [productcategory,setproductcategory] = useState('');
  const [rating,setrating] = useState('');
  const [stock,setstock] = useState('');
  const [imageFile, setImageFile] = useState(null); // State for image file
  const [sellerName, setSellerName] = useState("Seller123"); // Replace with seller's name dynamically

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get(`/api/products/${sellerName}`);
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  

  const handleImageChange = (e) => {
    setImageFile(e.target.files[0]); // Set the selected image file
  };

  const handleAddProduct = async () => {
    try {
      const formData = new FormData();
      formData.append('sellerName', sellerName);
      formData.append('name', newProduct.name);
      formData.append('price', newProduct.price);
      formData.append('category', newProduct.category);
      formData.append('rating', newProduct.rating);
      formData.append('productId', newProduct.productId);
      formData.append('inStockValue', newProduct.inStockValue);
      if (imageFile) {
        formData.append('img', imageFile); // Add the image file to the form data
      }

      await axios.post('/api/products', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      fetchProducts(); // Refresh the product list
      setNewProduct({
        sellerName: '',
        name: '',
        price: '',
        category: '',
        rating: 0,
        productId: '',
        inStockValue: 0,
      });
      setImageFile(null); // Reset the image file input
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  const handleUpdateStock = async (productId, newStock) => {
    try {
      await axios.patch(`/api/products/${productId}`, { inStockValue: newStock });
      fetchProducts(); // Refresh the product list
    } catch (error) {
      console.error('Error updating stock:', error);
    }
  };

  return (
    <div className="seller-product-container">
      <h1 className="seller-product-title">Manage Your Products</h1>

      <div className="seller-product-add-form">
        <h2>Add New Product</h2>
        <input
          type="text"
          name="name"
          placeholder="Product Name"
          value={productname}
          onChange={(e)=>{setproductname(e.target.value)}}
          className="seller-product-input"
        />
        <input
          type="text"
          name="price"
          placeholder="Price"
          value={productprice}
          onChange={(e)=>{setproductprice(e.target.value)}}
          className="seller-product-input"
        />
        <input
          type="text"
          name="category"
          placeholder="Category"
          value={productcategory}
          onChange={(e)=>{setproductcategory(e.target.value)}}
          className="seller-product-input"
        />
        <input
          type="number"
          name="rating"
          placeholder="Rating"
          value={rating}
          onChange={(e)=>{setrating(e.target.value)}}
          className="seller-product-input"
        />
       
        <input
          type="number"
          name="inStockValue"
          placeholder="Stock Value"
          value={stock}
          onChange={(e)=>{setstock(e.target.value)}}
          className="seller-product-input"
        />
        <input
          type="file"
          accept="image/*"
          onChange={handleImageChange}
          className="seller-product-file-input"
        />
        <button onClick={handleAddProduct} className="seller-product-add-button">
          Add Product
        </button>
      </div>

      <div className="seller-product-list">
        <h2>Your Products</h2>
        {products.map((product) => (
          <div key={product.productId} className="seller-product-item">
            <img src={product.img} alt={product.name} className="seller-product-image" />
            <div className="seller-product-details">
              <h3>{product.name}</h3>
              <p>Price: ${product.price}</p>
              <p>Category: {product.category}</p>
              <p>Rating: {product.rating}/5</p>
              <p>Stock: {product.inStockValue}</p>
              <input
                type="number"
                placeholder="Update Stock"
                min="0"
                className="seller-product-stock-input"
                onChange={(e) => handleUpdateStock(product.productId, e.target.value)}
              />
              <button
                onClick={() => handleUpdateStock(product.productId, product.inStockValue)}
                className="seller-product-update-button"
              >
                Update Stock
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SellerProductsPage;
