import { useState, useEffect } from "react";
import axios from "axios";
import Cookies from "js-cookie";
import { decodeToken } from "react-jwt";

const Wishlist = () => {
  const [wishlist, setWishlist] = useState([]);

  useEffect(() => {
    const fetchWishlist = async () => {
      const token = Cookies.get("authToken");
      if (token) {
        try {
          const decoded = decodeToken(token);
          const fetchemail = decoded.email;
          console.log("Fetched email:", fetchemail);

          try {
            const response = await axios.get("http://localhost:3020/getwishlistitems", {
              params: { email: fetchemail },
              withCredentials: true,
            });

            console.log("Fetched products:", response.data.updatedProducts);
            setWishlist(response.data.updatedProducts || []); // Update based on backend response
          } catch (error) {
            console.error("Error fetching wishlisted products:", error);
          }
        } catch (error) {
          console.error("Token validation error:", error);
        }
      } else {
        console.error("No auth token found");
      }
    };

    fetchWishlist();
  }, []);

  const handleRemoveFromWishlist = async (id) => {
    try {
      await axios.delete(`http://localhost:3020/api/wishlist/${id}`);
      const updatedWishlist = wishlist.filter((item) => item.id !== id);
      setWishlist(updatedWishlist);
    } catch (error) {
      console.error("Error removing item from wishlist:", error);
    }
  };

  const handleAddToCart = async (item) => {
    try {
      await axios.post("http://localhost:3020/api/cart", item);
      console.log(`${item.name} added to cart.`);
      handleRemoveFromWishlist(item.id); // Optionally remove from wishlist after adding to cart
    } catch (error) {
      console.error("Error adding item to cart:", error);
    }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "600px", margin: "0 auto" }}>
      <h2>Your Wishlist</h2>
      {wishlist.length === 0 ? (
        <p>Your wishlist is empty.</p>
      ) : (
        <ul style={{ listStyleType: "none", padding: 0 }}>
          {wishlist.map((item) => (
            <li
              key={item.id || item._id} // Adjust key as per the backend response
              style={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                padding: "10px",
                borderBottom: "1px solid #ccc",
              }}
            >
              <div>
                <img src={item.img} alt="" />
                <h4>{item.name}</h4>
                <p>Price: â‚¹{item.price}</p>
              </div>
              <div>
                <button
                  style={{ marginRight: "10px" }}
                  onClick={() => handleRemoveFromWishlist(item.id || item._id)}
                >
                  Remove from Wishlist
                </button>
                <button onClick={() => handleAddToCart(item)}>Add to Cart</button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Wishlist;
