import {useState,useEffect} from 'react';
import './Product.css'
import axios from 'axios'
import Cookies from 'js-cookie';
import {decodeToken} from 'react-jwt'
import {useNavigate} from 'react-router-dom';



const SellerProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [productname, setproductname] = useState('');
  const [productprice , setproductprice] = useState('');
  const [productcategory,setproductcategory] = useState('');
  const [rating,setrating] = useState('');
  const [stock,setstock] = useState('');
  const [imageFile, setImageFile] = useState(null); // State for image file
  const [sellerName, setSellerName] = useState("Seller123"); // Replace with seller's name dynamically

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get(`/api/products/${sellerName}`);
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  

  const handleImageChange = (e) => {
    setImageFile(e.target.files[0]); // Set the selected image file
  };

  const handleAddProduct = async () => {
    try {
      const formData = new FormData();
      formData.append('sellerName', sellerName);
      formData.append('name', productname);
      formData.append('price', productprice);
      formData.append('category', productcategory);
      formData.append('rating', rating);
      formData.append('inStockValue', stock);
      if (imageFile) {
        formData.append('img', imageFile); // Add the image file to the form data
      }

      await axios.post('/api/products', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      fetchProducts(); // Refresh the product list
       
      setImageFile(null); // Reset the image file input
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  const handleUpdateStock = async (productId, newStock) => {
    try {
      await axios.patch(`/api/products/${productId}`, { inStockValue: newStock });
      fetchProducts(); // Refresh the product list
    } catch (error) {
      console.error('Error updating stock:', error);
    }
  };

  
    return (
        <div className="seller-products-page">
          <h1 className="seller-products-title">Manage Your Products</h1>
      
          <div  className="seller-products-wrapper">
            {/* Add Product Form Container */}
            <div className="seller-products-form-container">
              <h2 className="seller-products-form-title">Add New Product</h2>
              <div className="seller-products-form">
                <input
                  type="text"
                  name="name"
                  placeholder="Product Name"
                  value={productname}
                  onChange={(e) => setproductname(e.target.value)}
                  className="seller-products-input"
                />
                <input
                  type="text"
                  name="price"
                  placeholder="Price"
                  value={productprice}
                  onChange={(e) => setproductprice(e.target.value)}
                  className="seller-products-input"
                />
                <input
                  type="text"
                  name="category"
                  placeholder="Category"
                  value={productcategory}
                  onChange={(e) => setproductcategory(e.target.value)}
                  className="seller-products-input"
                />
                <input
                  type="number"
                  name="rating"
                  placeholder="Rating"
                  value={rating}
                  onChange={(e) => setrating(e.target.value)}
                  className="seller-products-input"
                />
                <input
                  type="number"
                  name="inStockValue"
                  placeholder="Stock Value"
                  value={stock}
                  onChange={(e) => setstock(e.target.value)}
                  className="seller-products-input"
                />
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleImageChange}
                  className="seller-products-file-input"
                />
                <button
                  onClick={handleAddProduct}
                  className="seller-products-button"
                >
                  Add Product
                </button>
              </div>
            </div>
      
            {/* Product List Container */}
            <div className="seller-products-list-container">
              <h2 className="seller-products-list-title">Your Products</h2>
              <div className="seller-products-list">
                {products.map((product) => (
                  <div key={product.productId} className="seller-products-item">
                    <img
                      src={product.img}
                      alt={product.name}
                      className="seller-products-image"
                    />
                    <div className="seller-products-item-details">
                      <h3 className="seller-products-item-title">{product.name}</h3>
                      <p className="seller-products-item-price">Price: ${product.price}</p>
                      <p className="seller-products-item-category">Category: {product.category}</p>
                      <p className="seller-products-item-rating">Rating: {product.rating}/5</p>
                      <p className="seller-products-item-stock">Stock: {product.inStockValue}</p>
                      <input
                        type="number"
                        placeholder="Update Stock"
                        min="0"
                        className="seller-products-stock-input"
                        onChange={(e) =>
                          handleUpdateStock(product.productId, e.target.value)
                        }
                      />
                      <button
                        onClick={() =>
                          handleUpdateStock(product.productId, product.inStockValue)
                        }
                        className="seller-products-update-button"
                      >
                        Update Stock
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      );
      
  
  }

export default SellerProductsPage;
